package net.jeeshop.web.action.manage.navigation;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.navigation.NavigationService;import net.jeeshop.services.manage.navigation.bean.Navigation;public class NavigationAction extends BaseAction<Navigation> {	private static final long serialVersionUID = 1L;	private NavigationService navigationService;	public NavigationService getNavigationService() {		return navigationService;	}	protected void selectListAfter() {		pager.setPagerUrl("navigation!selectList.action");	}	public void setNavigationService(NavigationService navigationService) {		this.navigationService = navigationService;	}	public Navigation getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Navigation();		}		super.initPageSelect();	}	public void insertAfter(Navigation e) {		e.clear();	}		@Override	public String insert() throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.insert();	}		@Override	public String update() throws Exception {		e.setTarget("_blank");		e.setPosition("bottom");		return super.update();	}}